import { pgTable, serial, varchar, date, boolean, integer, timestamp } from "drizzle-orm/pg-core";
import { users } from "./users.js";
export const events = pgTable("events", {
    eventId: serial("event_id").primaryKey(),
    name: varchar("name", { length: 150 }).notNull(),
    eventType: varchar("event_type", { length: 20 }).$type().notNull(),
    date: date("date").notNull(),
    autoGenerated: boolean("auto_generated").default(false),
    createdBy: integer("created_by").references(() => users.userId, { onDelete: "set null" }),
    createdAt: timestamp("created_at").defaultNow(),
});
